mapper.insert:
alter table `data` AUTO_INCREMENT=1;
insert into `data` values
(null,#{dataTitle},#{dataDesc},#{image},#{createTime},#{industry},
#{price},#{type},#{downloadCount},#{dataCategoryId},#{dataClassId},#{dataKey},#{fileType},#{eTag});

UPDATE `data`
dataTitle=#{dataTitle}, dataDesc=#{dataDesc}, image=#{image}, createTime=#{createTime}, industry=#{industry}, price=#{price}, type=#{type}, downloadCount=#{downloadCount}, dataCategoryId=#{dataCategoryId}, dataClassId=#{dataClassId}, dataKey=#{dataKey}, fileType=#{fileType}, eTag=#{eTag},
);
