mapper.update:
UPDATE t_dynamic
typeId=#{typeId}, title=#{title}, content=#{content}, createTime=#{createTime}, typeName=#{typeName},
WHERE id=#{id}

mapper.select:
SELECT t.typeName, t.typeId, d.id, d.title, d.content, d.createTime, d.image FROM t_type t, t_dynamic d WHERE t.typeId = d.typeId AND d.typeId = #{typeId}
AND d.title LIKE CONCAT(CONCAT('%',#{name},'%'))
ORDER BY d.createTime DESC
